//时间

var mjwfdata = "";
var sytime   = "";
function mjwf_data() {
	$.ajax({
			type: "POST",
			url: mjwfKj,
			data: "lotteryid="+mjwfid+"&flag=mjwf",
			timeout: 9000,
			dataType: 'json',
			success: function (data) {
				mjwfdata = eval("("+data+")");
				sytime = Date.parse(new Date(mjwfdata.entime))/1000 - Date.parse(new Date(mjwfdata.datime))/1000;
				$('#mjwf_enissue').html(mjwfdata.enissue);
				$('#mjwf_opissue').html(mjwfdata.opissue);
				$('#mjwfForm_qs').val(mjwfdata.enissue);
				showadvbox();
				var oDate = diff(sytime);
				 $('#mjwf_entime').html('<span class="bet-btn bet-hour">'+ fftime(oDate.hour) +'</span><span class="bet-btn0">:</span><span class="bet-btn bet-minuter">'+fftime(oDate.minute)+'</span><span class="bet-btn0">:</span><span class="bet-btn bet-second">'+fftime(oDate.second)+'</span>');			
		}
	});
};
mjwf_data();

function count_setInterval(){
	setInterval(function () {
		mjwf_data();
		showadvbox();
		}, 10000)
};
count_setInterval();

function count_down(){
	count_down_time = setInterval(function () {
		if(sytime<=0){
			mjwf_data();
			showadvbox();
		};
		if(sytime==0){
			if($('#tk_mjwf').css('display') == 'block' ){
				layer.open({
					  icon: 0,
					  title: '提示',
					  content: '期号已改变。<br />投注时请注意期号！'
					}); 
			}
		}
		$('#mjwf_onissue').html($('#mjwf_enissue').html());
		 var oDate = diff(sytime--);
		if(sytime>=53&&sytime<=55){
					list3Data();
				}
		 $('#mjwf_entime').html('<span class="bet-btn bet-hour">'+ fftime(oDate.hour) +'</span><span class="bet-btn0">:</span><span class="bet-btn bet-minuter">'+fftime(oDate.minute)+'</span><span class="bet-btn0">:</span><span class="bet-btn bet-second">'+fftime(oDate.second)+'</span>');	
		}, 1000)
};
clearInterval(timerno);clearInterval(count_down_time);
count_down();
//时间
function diff(t) {
			return t > 0 ? {
				day: Math.floor(t / 86400),
				hour: Math.floor(t % 86400 / 3600),
				minute: Math.floor(t % 3600 / 60),
				second: Math.floor(t % 60)
			} : {
				day: 0,
				hour: 0,
				minute: 0,
				second: 0
			}
		}
//时间个位补零
function fftime(n) {
			return Number(n) < 10 ? "" + 0 + Number(n) : Number(n)
		}
//开奖号
function showadvbox(){
	var showadvbox = "";
	if (mjwfdata.code.length>1) {
		for (var i=0;i<mjwfdata.code.length;i++){
			showadvbox += '<span class="gr_s gr_sx" flag="move">'+mjwfdata.code[i]+'</span>'
		}
	}else{
		for (var i=0;i<mjwfs;i++){
			showadvbox += '<span class="gr_s gr_sx" flag="normal">x</span>'
		}
	}
	$('#mjwf_code').html(showadvbox);
};

